import React from 'react';
import './App.css';
import DogBar from './containers/DogBar'

class App extends React.Component {
  state = {
    dogs: [],
    dogClicked: false,
    filter: false
  }

  componentDidMount() {
    fetch('http://localhost:3000/pups')
      .then(resp => resp.json())
      .then(fetchedDogs => this.setState({ dogs: fetchedDogs }))
  }

  changeFilter = () => {
    this.setState({ filter: !this.state.filter })
  }

  selectedDog = (id) => {
    let premier = this.state.dogs.find(dog => dog.id === id)
    this.setState({ dogClicked: premier })
    console.log(premier)
  }

  render() {

    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter" onClick={this.changeFilter}>Filter good dogs: {this.state.filter ? 'ON' : 'OFF'}</button>
        </div>

        <div id="dog-bar">
          <DogBar dogs={this.state.filter ? this.state.dogs.filter(dog => dog.isGoodDog) : this.state.dogs} selectedDog={this.selectedDog} />
        </div>

        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            {/* {(this.state.selectedDogId !== '')
              ?
              <img src=
            } */}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
