import React from 'react';
import Dog from '../components/Dog'

class DogBar extends React.Component {
    state = {
        selectedDogId: ''
    }

    handleOnClick = (e) => {
        this.setState({ selectedDogId: e.target.id })
        this.props.selectedDog(this.state.selectedDogId)
    }

    render() {
        const { dogs } = this.props

        return (
            dogs.map(dog => <Dog key={dog.id} id={dog.id} dog={dog} handleOnClick={this.handleOnClick} />)
        )
    }
}

export default DogBar
