import React from 'react';
import './App.css';
import DogBar from './containers/DogBar'

class App extends React.Component {
  state = {
    dogs: []
  }

  componentDidMount() {
    fetch('http://localhost:3000/pups')
      .then(resp => resp.json())
      .then(fetchedDogs => this.setState({ dogs: fetchedDogs }))
  }

  selectedDog = (id) => {
    let newDogsArray = [...this.state.dogs]
    let premier = newDogsArray.find(dog => dog.id === id)
    console.log(newDogsArray)
  }

  render() {

    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>

        <div id="dog-bar">
          <DogBar dogs={this.state.dogs} selectedDog={this.selectedDog} />
        </div>

        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            {/* {(this.state.selectedDogId !== '')
              ?
              <img src=
            } */}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
