import React from 'react';
import './App.css';
import DogContainer from './containers/DogContainer'

class App extends React.Component {
  state = {
    dogs: [],
    selectedDog: null
  }

  componentDidMount() {
    fetch('http://localhost:3000/pups')
      .then(resp => resp.json())
      .then(fetchedDogs => this.setState({ dogs: fetchedDogs }))
  }

  selectedDog = (id) => {
    this.setState({ selectedDogId: e.target.id })
    return this.state.dogs.find(dog => dog.id === id)
  }

  render() {
    console.log(this.state.dogs)
    const selectDog = this.state.dogs.find(dog => dog.id === this.state.selectedDogId)
    console.log(selectDog.name)

    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>

        <div id="dog-bar">
          <DogContainer dogs={this.state.dogs} selectedDogId={this.state.selectedDogId} selectedDog={this.selectedDog} />
        </div>

        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            {/* {(this.state.selectedDogId !== '')
              ?
              <img src=
            } */}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
