import React from 'react';
import './App.css';

class App extends React.Component {
  state = {
    dogs: [],
    selectedDogId: ''
  }

  componentDidMount() {
    fetch('http://localhost:3000/pups')
      .then(resp => resp.json())
      .then(fetchedDogs => this.setState({ dogs: fetchedDogs }))
  }

  handleOnClick = (e) => {
    console.log(this.state.dogs.filter(dog => dog.id === e.target.id ? dog : null))
  }

  render() {

    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>

        <div id="dog-bar">
          {this.state.dogs.map(dog => <span key={dog.id} id={dog.id} onClick={this.handleOnClick}>{dog.name}</span>)}
        </div>

        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            {/* <img src= */}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
